query.CREATE_TABLES:
create table CONCERT_HALL ( ID INT UNSIGNED AUTO_INCREMENT, NAME VARCHAR(200) NOT NULL, LOCATION VARCHAR(200) NOT NULL, CAPACITY int UNSIGNED CHECK (PERSONNEL > 0), CONSTRAINT CONCERT_HALL_PK PRIMARY KEY (ID) );
create table CONCERT ( ID INT UNSIGNED AUTO_INCREMENT, CONCERT_HALL_ID INT UNSIGNED, NAME VARCHAR(200) NOT NULL, TYPE VARCHAR(200) NOT NULL, PRICE INT UNSIGNED CHECK (PRICE >= 0), PRIMARY KEY(ID), CONSTRAINT CONCERT_CONCERT_HALL_FK FOREIGN KEY (CONCERT_HALL_ID) REFERENCES CONCERT_HALL(ID) ON DELETE CASCADE ON UPDATE CASCADE );
create table AUDIENCE ( ID INT UNSIGNED AUTO_INCREMENT, NAME VARCHAR(200) NOT NULL, GENDER VARCHAR(1) NOT NULL, AGE INT UNSIGNED CHECK (AGE > 0), PRIMARY KEY(ID) );
create table RESERVATION ( CONCERT_ID int UNSIGNED, SEAT_NO int UNSIGNED CHECK (SEAT_NO > 0), AUDIENCE_ID int UNSIGNED, RESERVATION_YN varchar(1) DEFAULT 'N', PRIMARY KEY(CONCERT_ID, SEAT_NO), CONSTRAINT RESERVATION_CONCERT_FK FOREIGN KEY (CONCERT_ID) REFERENCES CONCERT(ID) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT RESERVATION_AUDIENCE_FK FOREIGN KEY (AUDIENCE_ID) REFERENCES AUDIENCE(ID) ON DELETE CASCADE ON UPDATE CASCADE );
